import React from 'react';
import { Modal, ModalBody, ModalHeader, ModalFooter } from 'reactstrap';
import { useCartState, useCartDispatch } from '../../context/CartContext';

const Cart = ({ isOpen, toggle }) => {
  const { cart } = useCartState();
  const dispatch = useCartDispatch();

  const handleRemoveFromCart = (productId) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: { id: productId } });
  };

  const handleClearCart = () => {
    dispatch({ type: 'CLEAR_CART' });
  };

  return (
    <Modal isOpen={isOpen} toggle={toggle} centered>
      <ModalHeader toggle={toggle}>Giỏ hàng của bạn</ModalHeader>
      <ModalBody>
        {cart.length === 0 ? (
          <p>Giỏ hàng của bạn đang trống.</p>
        ) : (
          <ul>
            {cart.map(item => (
              <li key={item.id}>
                <div>
                  <img src={item.photo} alt={item.title} style={{ width: '50px', height: '50px' }} />
                  <span>{item.title}</span>
                </div>
                <div>
                  <span>Số lượng: {item.quantity}</span>
                  <span>Giá tiền: {item.price}</span>
                </div>
                <button onClick={() => handleRemoveFromCart(item.id)}>Xóa</button>
              </li>
            ))}
          </ul>
        )}
      </ModalBody>
      <ModalFooter>
        <button onClick={handleClearCart}>Xóa tất cả</button>
      </ModalFooter>
    </Modal>
  );
};

export default Cart;
