import React from 'react';
import { Modal, ModalBody,ModalHeader  } from 'reactstrap'; 
import product_list from '../assets/data/products-list'
import './popup.css'
const Popup = ({ isOpen, toggle, productId }) => {

  const product = product_list.find((product) => product.id===productId) 
  
  return (
    <Modal isOpen={isOpen} toggle={toggle}>
      <ModalBody>
          <h5 className='title-popup'>{product ? product.title : " "}</h5>
          <i className="close_btn ri-close-line" onClick={toggle} style={{ cursor: 'pointer', marginLeft: 'auto'}}></i>

        <button onClick={toggle}>Đặt hàng</button>
        <button onClick={toggle}>Hủy</button>
      </ModalBody>
    </Modal>
  );
};

export default Popup;
