import React from 'react'
import { Container, Row, Col } from "reactstrap";

const Login = () => {
  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
      <Row>
        <Col lg='5'> {/* Giới hạn kích thước cột */}
          <div className="login-form text-center"> {/* Sử dụng lớp CSS riêng để tùy chỉnh */}
            <h2>Đăng nhập</h2>
            <form className="formdangnhap">
              <div className='mb-3'>
                <label>Tên đăng nhập:</label>
                <input type="text" name="" className='' /> {/* Sử dụng form-control của Bootstrap */}
              </div>
              <div className='mb-3'>
                <label>Mật khẩu:</label>
                <input type="password" className='form-control' />
              </div>
              <button className="btn">Đăng nhập</button> {/* Sử dụng lớp btn */}
            </form>
          </div>
        </Col>
      </Row>
    </Container>
  )
}

export default Login;
