import React from 'react'
import Slider from 'react-slick';

import slider1 from '../assets/images/slider.jpg'
import slider2 from '../assets/images/slider2.jpg'
import slider3 from '../assets/images/slider3.jpg'

const ImageSlider = () => {
    // Cấu hình cho slider
    const settings = {
      dots: true, // Hiển thị dấu chấm để điều hướng
      infinite: true, // Cho phép slider lặp lại
      speed: 500, // Tốc độ chuyển đổi giữa các slide (ms)
      slidesToShow: 1, // Số lượng slide hiển thị mỗi lần
      autoplay: true, // Tự động chạy
      autoplaySpeed: 3000, // Thời gian giữa các lần chuyển đổi (ms)
    };
  
    // Dữ liệu ảnh (có thể thay thế bằng dữ liệu động từ API hoặc props)
    const images = [
        { 
            id: 1, 
            src: {slider1}
        },
        { 
            id: 2, 
            src: {slider2}
        },
        {
            id: 3, 
            src: {slider3}
        },
    ];
  
    return (
      <Slider {...settings}>
        {images.map((image) => (
          <div key={image.id}>
            <img src={image.src} alt={image.alt} className='w-100' />
          </div>
        ))}
      </Slider>
    );
  };
  
  export default ImageSlider;
