import React from 'react';
import { Modal, ModalBody, ModalHeader, ModalFooter } from 'reactstrap';
import { useCart } from '../../context/CartContext';
import './cart.css';

const Cart = ({ isOpen, toggle }) => {
  const { state: cartState } = useCart();

  const totalAmount = cartState.cart.reduce((acc, item) => acc + (item.price * item.quantity), 0);

  return (
    <Modal isOpen={isOpen} toggle={toggle} centered>
      <ModalHeader toggle={toggle}>
        <h5>Giỏ hàng của bạn</h5>
      </ModalHeader>
      <ModalBody>
        {cartState.cart.length > 0 ? (
          <div className="cart-items">
            {cartState.cart.map((item, index) => (
              <div className="cart-item" key={index}>
                <img src={item.photo} alt={item.title} className="cart-item-image" />
                <div className="cart-item-details">
                  <h6>{item.title}</h6>
                  <span>Kích cỡ: {item.size}</span>
                  <p>Kiểu uống: {item.type}</p>
                  <p>Số lượng: {item.quantity}</p>
                  <p>Giá: {item.price.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p>Giỏ hàng của bạn đang trống.</p>
        )}
        <div className="cart-total">
          <h6>Tổng cộng: {totalAmount.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}</h6>
        </div>
      </ModalBody>
      <ModalFooter>
        <button onClick={toggle} className='btn_cart'>Đóng</button>
        <button onClick={toggle} className='btn_cart'>Thanh toán</button>
      </ModalFooter>
    </Modal>
  );
};

export default Cart;
