import React, { useState } from "react";
import { Container, Row, Button } from "reactstrap";
import { NavLink, Link } from "react-router-dom";

import logo from "../../assets/images/logo.jpg";
import "./header.css";

const nav__links = [
  {
    path: "/home",
    display: "Trang chủ",
  },
  {
    path: "/product",
    display: "Sản phẩm",
  },
];

const Header = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen); /* Chuyển đổi trạng thái menu */
  };

  return (
    <header className="header">
      <Container>
        <Row className="d-flex justify-content-between align-items-center"> 
          <div className="logo">
            <Link to="/home"><img src={logo} alt="" /></Link>
          </div>

          <div className={`navifation ${menuOpen ? "menu-open" : ""}`}> /* Class cho menu */
            <ul className="menu d-flex align-items-center gap-5"> /* Bố cục theo hàng */
              {nav__links.map((item, index) => (
                <li className="nav_item" key={index}> /* Dùng key cho mỗi mục */
                  <NavLink 
                    to={item.path}
                    className={(navClass) => navClass.isActive ? "active_link" : ""}
                  >
                    {item.display}
                  </NavLink>
                </li>
              ))}
            </ul>
          </div>

          <div className="nav_right d-flex align-items-center gap-4"> /* Bootstrap class */
            <div className="nav_btns d-flex align-items-center gap-4"> /* Bootstrap class */
              <Button className="btn secondary__btn">
                <Link to="/login">Đăng nhập</Link>
              </Button>
              <Button className="btn primary__btn">
                <Link to="/register">Đăng ký</Link>
              </Button>

              <div>
                <span className="mobile_menu" onClick={toggleMenu}> /* Nút menu dạng trượt */
                  <i className="ri-menu-line"></i> /* Biểu tượng menu */
                </span>
              </div>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
