import React from 'react';
import { Modal, ModalBody } from 'reactstrap'; // Sử dụng thư viện Reactstrap hoặc tương tự

const Popup = ({ isOpen, toggle, productId }) => {
  // Xử lý hàm đặt hàng
  const handleOrder = () => {
    // Thực hiện đặt hàng cho sản phẩm với ID là productId
    console.log('Đang đặt hàng cho sản phẩm có ID:', productId);
    // Đóng modal sau khi đặt hàng thành công hoặc bất kỳ hành động nào khác
    toggle();
  };

  return (
    <Modal isOpen={isOpen} toggle={toggle}>
      <ModalBody>
        <h5>Bạn có muốn đặt hàng sản phẩm này?</h5>
        <button onClick={toggle}>Đặt hàng</button>
        <button onClick={toggle}>Hủy</button>
      </ModalBody>
    </Modal>
  );
};

export default Popup;
