import React from 'react';
import { Modal, ModalHeader, ModalBody, ModalFooter, Button } from 'reactstrap';
import { useCart } from '../../context/CartContext';
import './cart.css';

const Cart = ({ isOpen, toggle }) => {
  const { state: { cart }, dispatch } = useCart();

  const handleRemoveFromCart = (id) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: id });
  };

  return (
    <Modal isOpen={isOpen} toggle={toggle} centered>
      <ModalHeader toggle={toggle}>Giỏ hàng</ModalHeader>
      <ModalBody>
        {cart.length === 0 ? (
          <p>Giỏ hàng trống</p>
        ) : (
          cart.map(item => (
            <div key={item.id} className="cart-item">
              <img src={item.photo} alt={item.title} className="cart-item-image" />
              <div className="cart-item-details">
                <h5>{item.title}</h5>
                <p>{item.quantity} x {item.size} - {item.type}</p>
                <p>{item.price.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}</p>
              </div>
              <Button color="danger" onClick={() => handleRemoveFromCart(item.id)}>Xóa</Button>
            </div>
          ))
        )}
      </ModalBody>
      <ModalFooter>
        <Button color="secondary" onClick={toggle}>Đóng</Button>
      </ModalFooter>
    </Modal>
  );
};

export default Cart;
