import React from 'react';
import { Modal, ModalBody,ModalHeader,ModalFooter, } from 'reactstrap'; 
import product_list from '../assets/data/products-list'
import './popup.css'
const Popup = ({ isOpen, toggle, productId }) => {

  const closeBtn = (
    <button className="close" onClick={toggle} type="button">
      &times;
    </button>
  );
  const product = product_list.find((product) => product.id===productId) 
  
  return (
    <Modal isOpen={isOpen} toggle={toggle} centered>
      <ModalHeader close={closeBtn}>
          <h5 className='title_popup'>{product ? product.title : " "}</h5>
      </ModalHeader>
      {<ModalBody>
          <img src={product ? product.photo : " "} className='pic' />
             <div className="booking">
             <span className='option'><strong>Kích cỡ:</strong></span>
             <span className='size'>S</span>
             <span className='size'>M</span>
             
             <span className='option'><strong>Kiểu uống:</strong></span>
             <span className='size'>Nóng</span>
             <span className='size'>Lạnh</span>
             </div>
      </ModalBody> }
      <ModalFooter>
        <button onClick={toggle}>Đặt hàng</button>
        <button onClick={toggle}>Hủy</button>
      </ModalFooter>
    </Modal>
  );
};

export default Popup;
