import React from 'react';
import { Modal, ModalBody, ModalHeader, ModalFooter, Button } from 'reactstrap';
import { useCartState, useCartDispatch } from '../../context/CartContext';

const Cart = ({ isOpen, toggle }) => {
  const { cart } = useCartState();
  const dispatch = useCartDispatch();

  const handleRemoveFromCart = (productId) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: { id: productId } });
  };

  const handleClearCart = () => {
    dispatch({ type: 'CLEAR_CART' });
  };

  const handleClose = () => {
    toggle();
  };

  const handleCheckout = () => {
    // Implement checkout functionality
  };

  return (
    <Modal isOpen={isOpen} toggle={handleClose} centered>
      <ModalHeader toggle={handleClose}>Giỏ hàng của bạn</ModalHeader>
      <ModalBody>
        {cart.length === 0 ? (
          <p>Giỏ hàng của bạn đang trống.</p>
        ) : (
          <div>
            {cart.map(item => (
              <div key={item.id} style={{ marginBottom: '20px' }}>
                <img src={item.photo} alt={item.title} style={{ width: '100px', height: '100px', marginRight: '20px' }} />
                <div style={{ display: 'inline-block' }}>
                  <h5>{item.title}</h5>
                  <p>Size: {item.size}</p>
                  <p>Kiểu uống: {item.type}</p>
                  <p>Số lượng: {item.quantity}</p>
                  <p>Tổng tiền: {item.price}</p>
                  <Button color="danger" onClick={() => handleRemoveFromCart(item.id)}>Xóa</Button>
                </div>
              </div>
            ))}
          </div>
        )}
      </ModalBody>
      <ModalFooter>
        <Button color="secondary" onClick={handleClearCart}>Xóa tất cả</Button>
        <Button color="primary" onClick={handleCheckout}>Thanh toán</Button>
      </ModalFooter>
    </Modal>
  );
};

export default Cart;
